        this.proportions_text = "3:5 " + this.translate("Ratio");
        if (this.proportions == "fibonacci")
          this.proportions_text += this.translate(
            "<br>The proportion is linked to the Fibonacci Sequence"
          );
        this.proportions_fibo_active.push(3);
        this.proportions_fibo_active.push(5);
      } else if (r >= 1.581 && r <= 1.64) {
