          this.proportions_text += this.translate(
            "<br>The proportion is the absolute Fibonacci Sequence"
          );
        this.proportions_fibo_active_golden = true;
      } else {
        var xx = parseFloat(this.proportions_x);
        var yy = parseFloat(this.proportions_y);
        if (isNaN(xx)) xx = 1;
        if (isNaN(yy)) yy = 1;

