    back() {
      if (this.step == 99) {
        this.step = 3;
        return;
      } else if (this.step == 121) {
        if (!this.is_resume) {
          this.step12_1_substep = 0;
          if (
            typeof this.graphic_element_d[this.step12_1_substep] !== "undefined"
          ) {
            this.graphic_element_b64 =
              this.graphic_element_d[this.step12_1_substep].b64;
            this.graphic_element_title =
              this.graphic_element_d[this.step12_1_substep].title;
            this.graphic_element_url =
              this.graphic_element_d[this.step12_1_substep].icon;
            this.graphic_element_uploaded = true;
          }
        }
        this.step = 12;
        return;
      } else if (this.step == 13 && this.bb_version == 3) {
        this.step = 121;
        return;
      }
      if (this.step == 6) {
        if (!this.is_resume) {
          this.step = 3;
        } else {
          if (this.logo_uploaded == true && this.step6_substep == 0) {
            this.logo_uploaded = false;
          } else if (this.step6_substep > 0) {
            this.step6_substep = 0;
            this.logo_uploaded = true;
            this.logo_versions = [];
          } else if (this.step6_substep == 0 && this.logo_uploaded == false) {
            this.step = 3;
          }
        }
        this.get_white_logo_variation(this.logo);
        return;
      }
      if (this.step == 10 && !this.all_logo_variations.length) {
        this.step = 8;
        return;
      }
      if (this.step == 10) {
        if (this.icon) {
          this.icon = null;
          this.icon_b64 = null;
          return;
        }
      }

      if (this.step != 1) {
        this.step--;
      } else {
        this.hide();
      }
      if (this.step == 4) this.step = 6;
      if (this.step == 5) this.step = 3;
      if (this.step == 6) {
        this.step6_substep = 0;
      }
      if (
        this.step == 6 &&
        (!this.is_resume || (this.is_resume && !this.is_first_forward))
      ) {
        if (!this.is_resume) this.logo_versions = [];
      }
      if (
        this.step == 9 &&
        (!this.is_resume || (this.is_resume && !this.is_first_forward))
      ) {
        if (!this.is_resume) {
          this.approved = [];
          this.approve_variation_number = 0;
        }
      }

      if (
        this.step == 11 &&
        (!this.is_resume || (this.is_resume && !this.is_first_forward))
      ) {
        if (!this.is_resume) {
          this.approved_icon = [];
          this.approve_variation_number_icon = 0;
        }
      }
      if (
        this.step == 16 &&
        (!this.is_resume || (this.is_resume && !this.is_first_forward))
      ) {
        if (!this.is_resume) {
          this.misuses = [];
          this.misuse_index = 0;
        }
      }
      if (this.step == 12) {
        if (!this.is_resume) {
          this.step12_substep = 0;
          /*this.icon_family = []
                     this.icon_family_uploaded = false
                     this.icon_family_title = 'Icon '+(this.icon_family.length+1)
                     this.icon_family_url = ''
                     this.icon_family_b64 = ''*/
          if (typeof this.icon_family[this.step12_substep] !== "undefined") {
            this.icon_family_b64 = this.icon_family[this.step12_substep].b64;
            this.icon_family_uploaded = true;
            this.icon_family_title =
              this.icon_family[this.step12_substep].title;
            this.icon_family_url = this.icon_family[this.step12_substep].icon;
            this.icon_family_uploaded = true;
          }
        }
      }
      if (this.step == 7) {
        if (!this.is_resume) {
          if (
            typeof this.new_color_scheme[0] !== "undefined" &&
            typeof this.colors_list[3] !== "undefined"
          )
            this.new_color_scheme[0].color.hex = this.colors_list[3].id;
          if (
            typeof this.new_color_scheme[1] !== "undefined" &&
            typeof this.colors_list[1] !== "undefined"
          )
            this.new_color_scheme[1].color.hex = this.colors_list[1].id;
          if (
            typeof this.new_color_scheme[2] !== "undefined" &&
            typeof this.colors_list[0] !== "undefined"
          )
            this.new_color_scheme[2].color.hex = this.colors_list[0].id;
          if (
            typeof this.new_color_scheme[3] !== "undefined" &&
            typeof this.colors_list[2] !== "undefined"
          )
            this.new_color_scheme[3].color.hex = this.colors_list[2].id;
          if (
            typeof this.new_color_scheme[4] !== "undefined" &&
            typeof this.colors_list[4] !== "undefined"
          )
            this.new_color_scheme[4].color.hex = this.colors_list[4].id;
        }
      }
      if (this.step == 24) this.step--;
    },

    close() {
