    forward_core_values(type) {
      const mockup_steps = 3;
      const core_values_steps = 5;

      var max_steps = type === "mockup" ? mockup_steps : core_values_steps;
      var block_class = type === "mockup" ? "mockup" : "core-value";

      if (max_steps == this.mobile_step || !this.detectMob()) {
        this.mobile_step = 0;
        this.forward();
      } else {
        $("." + block_class).hide();
        this.mobile_step++;
        $("." + block_class + "[data-index=" + this.mobile_step + "]").show();
      }
    },
    forward(step) {
      if (step) {
        this.step = step;
        this.save();
        return step;
      }
      console.log(this.step, this.bb_version);
      if (this.is_resume) this.is_first_forward = true;
      if (this.validate()) {
        if (this.step == 7) {
            // Only populate colors_list if it is empty
            if (!this.colors_list || this.colors_list.length === 0) {
                var keys = [2, 1, 3, 0, 4];
                for (var i in keys) {
                    var item = this.new_color_scheme[keys[i]];
                    this.colors_list.push({
                        id: item.color.hex,
                        is_new: false,
                        is_pinned: false,
                        show_shades: true,
                        is_primary: false,
                        color: {
                            loaded: false,
                            hex: {
                                value: item.color.hex,
                            },
                        },
                    });
                }
            }
        }

        if (this.step == 3 && this.state == "rebrand") {
          this.step = 99;
        } else if (this.step == 99 && this.state == "rebrand") {
          this.step = 4;
        } else if (this.step == 12 && this.bb_version == 3) {
          this.step = 121;
        } else if (this.step == 121 && this.bb_version == 3) {
          this.step = 13;
        } else {
          this.step++;
        }
        if (this.step == 4) this.step = 6;
        if (this.step == 5) this.step = 3;
        if (this.step == 6) {
          //this.logo_versions = []
          //this.all_logo_variations = []
        }
        if (this.step == 9) {
          this.approved = [];
          this.approve_variation_number = 0;
        }
        if (this.step == 10) {
          if (!this.icon) {
            this.icon_caption = null;
            this.approved_icon = [];
          }
        }
        if (this.step == 11) {
          this.approved_icon = [];
          this.approve_variation_number_icon = 0;
        }
        if (this.step == 12) {
          this.step12_substep = 0;
          /*this.icon_family = []
                     this.icon_family_uploaded = false
                     this.icon_family_title = 'Icon '+(this.icon_family.length+1)
                     this.icon_family_url = ''
                     this.icon_family_b64 = ''*/
          if (typeof this.icon_family[this.step12_substep] !== "undefined") {
            this.icon_family_b64 = this.icon_family[this.step12_substep].b64;
            this.icon_family_uploaded = true;
            this.icon_family_title =
              this.icon_family[this.step12_substep].title;
            this.icon_family_url = this.icon_family[this.step12_substep].icon;
            this.icon_family_uploaded = true;
          }
        }
        if (this.step == 121) {
          this.step12_1_substep = 0;
          if (
            typeof this.graphic_element_d[this.step12_1_substep] !== "undefined"
          ) {
            this.graphic_element_b64 =
              this.graphic_element_d[this.step12_1_substep].b64;
            this.graphic_element_title =
              this.graphic_element_d[this.step12_1_substep].title;
            this.graphic_element_url =
              this.graphic_element_d[this.step12_1_substep].icon;
            this.graphic_element_uploaded = true;
          }
        }
        if (this.step == 16) {
          this.misuses = [];
          this.misuse_index = 0;
        }

        this.save();
      }
    },
    hide() {
      if (this.step == 1) {
        this.save();
      }
      window.location = "/my";
    },
    processGeneratePdf() {
      clearInterval(this.processing_inteval);
      axios
        .post(this.endpoints.ajax.post.brandbook.generate, {
          id: this.id,
          theme: this.theme,
          all: true,
        })
        .then((response) => {
          this.pdf_links = response.data;
          this.forward();
          // this.pdf_link = response.data.url
        })
        .catch((error) => {
          console.error(error);
          clearInterval(this.processing_inteval);
          this.processingWidth = 0;
          this.processing_inteval = setInterval(() => {
            if (this.processingWidth >= 100) {
              clearInterval(this.processing_inteval);
            }

            this.processingWidth++;
          }, 100);
          axios
            .post(this.endpoints.ajax.post.brandbook.generate, {
              id: this.id,
              theme: this.theme,
              all: true,
            })
            .then((response) => {
              this.pdf_links = response.data;
              this.forward();
              // this.pdf_link = response.data.url
            });
        });
      this.processing_inteval = setInterval(() => {
        if (this.processingWidth >= 100) {
          clearInterval(this.processing_inteval);
        }

        this.processingWidth++;
      }, 100);
    },
