          secondary_color_hex: this.secondary_color,
          step: this.step,
          new_color_scheme: this.new_color_scheme,
          mockups: this.mockups,
          icon_family: this.icon_family,
          graphic_element: this.graphic_element_d,
          missuses: this.misuses,
          logo_versions: this.logo_versions,
          voices: this.voices,
        })
        .then(() => {
          if (this.theme_selected_next)
            this.$router.push({ name: "Preview", params: { id: this.id } });

            if(this.step === 24 && this.$route.name === 'Wizard'){
                if(this.bb_version == 1){
                    this.$router.push({ name: "Preview", params: { id: this.id } });
                }else if(this.bb_version == 2){
                    this.$router.push({ name: "PreviewNew", params: { id: this.id } });
                }else{
                    this.$router.push({ name: "PreviewNewV3", params: { id: this.id } });
                }
            }

          this.process_save_request = false;

          if (this.need_save_request == true) {
            this.need_save_request = false;
            this.save();
          }
        })
        .catch((error) => {
          this.process_save_request = false;

          if (this.need_save_request == true) {
            this.need_save_request = false;
            this.save();
          }
        });
