    search_industry(input) {
      if (input.length < 3) return [];

      var level = 2;
      if (this.step == 4) {
        level = 1;
        this.autocomplete_industry_level_1 = true;
      } else {
        this.autocomplete_industry_level_2 = true;
      }

      return axios
        .get(this.endpoints.ajax.get.industries + "?q=" + input + "&level=" + level)
        .then((response) => {
          this.autocomplete_industry_level_1 = false;
          this.autocomplete_industry_level_2 = false;
          return response.data;
        });
