          img.width = 200;
          img.height = 200;

          //detecting quantity of colors
          const regex = /#([0-9A-Za-z]{3,6})/gm;
          let m;
          let colors_used = [];
          while ((m = regex.exec(this.logo)) !== null) {
            if (m.index === regex.lastIndex) {
              regex.lastIndex++;
            }
            colors_used.push(m[0]);
          }
          this.colors_used = _.uniq(colors_used);

          if (this.colors_used.length > 3)
            this.colors_used = _.take(this.colors_used, 3);

          this.colors_count = this.colors_used.length;

          // this.colors_list = _.union(this.colors_used, this.generated_colors)
          // this.make_color_list()

          try {
