              ctx.beginPath();
              ctx.rect(0, 0, 200, 200);
              ctx.fillStyle = "white";
              ctx.fill();
              ctx.drawImage(img, 0, 0, 200, 200);

              let png = canvas.toDataURL("image/png");

              if (this.logo_versions.length == 0) {
                RGBaster.colors(png, {
                  exclude: ["rgb(255,255,255)"],
                  success: (payload) => {
                    // console.log(payload)
