                    this.main_color = "#" + color;
                    this.main_color_hist = "#" + color;
                    this.colors_used[0] = this.main_color;
                    var hex = payload.secondary
                      .replace("rgba", "")
                      .replace("rgb", "")
                      .replace("(", "")
                      .replace(")", "")
                      .split(",");
                    var rgb = {
                      r: parseInt(hex[0]),
                      g: parseInt(hex[1]),
                      b: parseInt(hex[2]),
                    };
                    var color = simpleColorConverter({
                      rgb: rgb,
                      to: "hex",
                    });
                    this.secondary_color = "#" + color;
                    this.secondary_color_hist = "#" + color;
                    this.colors_used[1] = this.secondary_color;

                    var hex = payload.palette[1]
                      .replace("rgba", "")
                      .replace("rgb", "")
                      .replace("(", "")
                      .replace(")", "")
                      .split(",");
                    var rgb = {
                      r: parseInt(hex[0]),
                      g: parseInt(hex[1]),
                      b: parseInt(hex[2]),
                    };
                    var color = simpleColorConverter({
                      rgb: rgb,
                      to: "hex",
                    });
                    this.third_color = "#" + color;
                    this.third_color_hist = "#" + color;
                    this.colors_used[2] = this.third_color;
                    setTimeout(() => {
                      this.upload_progress = 0;
                      this.logo_b64 =
                        "data:image/svg+xml;base64," + btoa(this.logo);
                      this.logo_uploaded = true;
                      this.make_color_list();
                      this.generateNewColorScheme();
                      // this.generate_colors()
                    }, 100);
                  },
