              } else {
                this.upload_progress = 0;
                this.logo_b64 = "data:image/svg+xml;base64," + btoa(this.logo);
                this.logo_uploaded = true;
              }
            };
          } catch (e) {
            console.error(e);
            this.upload_progress = 0;
            this.showErrorModal({
              title: this.translate(
