    logo_approve_forward() {
      if (this.step == 99) {
        this.logo_uploaded = false;
        this.step = 6;
        this.step6_substep = 0;
        this.white_bg_logo_type = null;
        return;
      }

      let white_logo_variation = this.get_white_logo_variation(this.logo);

      this.logo_versions[this.step6_substep] = {
        logo_b64: this.logo_b64,
        logo: this.logo,
        title: this.logo_title,
        white_bg_logo_type: this.white_bg_logo_type
          ? this.white_bg_logo_type
          : "original",
        white_bg_logo: white_logo_variation,
      };
      if (this.approved === null) {
        this.approved = [];
      }
      this.approved[this.step6_substep] = {
        logo_b64: this.logo_b64,
        logo: this.logo,
        title: this.logo_title,
        background: "transparent",
        white_bg_logo_type: this.white_bg_logo_type,
        white_bg_logo: white_logo_variation,
      };

      this.step6_substep++;
      this.white_bg_logo_type = null;

      if (this.step6_substep == 1) {
        this.logo_title = this.translate("Alternative Logo");
        if (this.logo_versions[1] !== undefined) {
          this.logo = this.logo_versions[1].logo;
          this.logo_b64 = this.logo_versions[1].logo_b64;
          this.logo_uploaded = true;
          return;
        }
      }
      if (this.step6_substep == 2) {
        this.logo_title = this.translate("Alternative Logo");
        if (this.logo_versions[2] !== undefined) {
          this.logo = this.logo_versions[2].logo;
          this.logo_b64 = this.logo_versions[2].logo_b64;
          this.logo_uploaded = true;
          return;
        }
      }
      if (this.step6_substep == 3) {
        // this.approved = []
        // this.rejected = []
        this.approve_variation_number = 0;
        this.logo_upload_skip();
      } else {
        this.logo_uploaded = false;
      }
    },
    logo_upload_skip() {
