    generate_colors() {
      var tmp_group = [];
      // this.colors_list = []
      // tmp_group.push({'id': this.main_color, 'color': {hex: {value: this.main_color}, loaded: false}, 'is_primary': false, 'is_pinned': false, 'loaded': true, 'is_new': false})
      axios
        .get(
          "https://www.thecolorapi.com/scheme?mode=" +
            this.color_scheme_mode +
            "&hex=" +
            this.main_color.replace("#", "") +
            "&count=" +
            4
        ) //(5-this.colors_used.length+1))
        .then((response) => {
          // this.generated_colors = response.data.colors
          var i = 0;
          if (!this.colors_list[i].is_pinned) {
            tmp_group.push({
              id: response.data.seed.hex.value,
              color: response.data.seed,
              is_primary: false,
              is_pinned: false,
              loaded: true,
              is_new: false,
            });
          } else {
            tmp_group.push(this.colors_list[i]);
          }
          response.data.colors.forEach((color) => {
            i++;
            if (!this.colors_list[i].is_pinned) {
              tmp_group.push({
                id: color.hex.value,
                color: color,
                is_primary: false,
                is_pinned: false,
                loaded: true,
                is_new: false,
              });
            } else {
              tmp_group.push(this.colors_list[i]);
            }
          });
          this.colors_list = tmp_group;
          // this.$root.$emit('colors.redraw')
        });
    },
    make_color_list() {
      var tmp_group = [];
      this.colors_list = [];

      axios
        .get(
          "https://www.thecolorapi.com/scheme?mode=" +
            this.color_scheme_mode +
            "&hex=" +
            this.main_color.replace("#", "") +
            "&count=" +
            (5 - this.colors_count)
        ) //(5-this.colors_used.length+1))
        .then((response) => {
          this.colors_used.forEach((color) => {
            tmp_group.push({
              id: color,
              color: { hex: { value: color }, loaded: false },
              is_primary: false,
              is_pinned: false,
              is_new: false,
            });
          });
          this.generated_colors = response.data.colors;
          this.generated_colors.forEach((color) => {
            tmp_group.push({
              id: color.hex.value,
              color: color,
              is_primary: false,
              is_pinned: false,
              is_new: false,
            });
          });
          this.colors_list = tmp_group;
        });
    },
    find_color(clr) {
      // var res = false
      if (this.colors_list[clr] != undefined) return this.colors_list[clr];
      return false;
    },
    is_color_similar_to_existing(clr, arr) {
      var _return = false;
      arr.forEach((item) => {
        if (clr.loaded) {
          if (item.loaded) {
            if (
              item.color.name.value == clr.name.value ||
              item.color.hex.value == clr.hex.value ||
              simpleColorConverter({
                hex3: item.color.hex.value,
                to: "pantone",
              }) ==
                simpleColorConverter({
                  hex3: clr.hex.value,
                  to: "pantone",
                })
            )
              _return = true;
          }
        } else {
          if (item.loaded) {
            if (
              item.color.hex.value == clr ||
              simpleColorConverter({
                hex3: item.color.hex.value,
                to: "pantone",
              }) ==
                simpleColorConverter({
                  hex3: clr,
                  to: "pantone",
                })
            )
              _return = true;
          }
        }
      });
      return _return;
    },
    add_color() {
      this.colors_list.push({
        is_pinned: false,
        is_new: true,
        is_primary: false,
        id: "#ffffff",
        color: {
          hex: {
            value: "#ffffff",
          },
          loaded: false,
        },
      });
      // this.show_color_picker = true
    },
    scroll_colors_right() {
      var max_steps = document.querySelector(".scroll-block").offsetWidth / 188;
      this.colors_panel_scroll++;
      if (
        this.colors_panel_scroll - Math.floor(max_steps) >
        this.colors_list.length + 1
      ) {
        this.colors_panel_scroll = Math.abs(
          this.colors_list.length + 1 - max_steps
        );
      }
    },
    scroll_colors_left() {
      this.colors_panel_scroll--;
      if (this.colors_panel_scroll < 0) this.colors_panel_scroll = 0;
    },
