    fibonacci_enabled() {
      var r = this.logo_w / this.logo_h;
      var proportions_x = "";
      var proportions_y = "";
      var proportions_text = "";
      var proportions_fibo_active = false;
      if (r >= 0.613 && r <= 0.86) {
        proportions_x = "x";
        proportions_y = "1.618 x";
      } else {
        if (r > 1) {
          var propo = r % 1 !== 0 ? r.toFixed(1) : Math.ceil(r);
          if (propo - Math.floor(propo) == 0) propo = Math.floor(propo);
          proportions_x = propo + "x";
          if (proportions_x == "1x") proportions_x = "x";
          proportions_y = "x";
        } else {
          var propo = (1 / r) % 1 !== 0 ? (1 / r).toFixed(1) : Math.ceil(1 / r);
          if (propo - Math.floor(propo) == 0) propo = Math.floor(propo);
          proportions_y = propo + "x";
          if (proportions_y == "1x") proportions_y = "x";
          proportions_x = "x";
        }
      }

      if (r >= 0.95 && r <= 1.05) {
        proportions_text = this.translate("1:1 Square Ratio");
        if (this.proportions == "fibonacci")
          proportions_text += this.translate(
            "<br>The proportion is linked to the Fibonacci Sequence"
          );
        proportions_fibo_active = true;
      } else if (r >= 2.9 && r <= 3.1) {
        proportions_text = "1:3 " + this.translate("Ratio");
        if (this.proportions == "fibonacci")
          proportions_text += this.translate(
            "<br>The proportion is linked to the Fibonacci Sequence"
          );
        proportions_fibo_active = true;
      } else if (r >= 4.8 && r <= 5.2) {
        var numratio = parseFloat(proportions_x);
        if (isNaN(numratio)) numratio = 1;

        numratio = Math.round((r + Number.EPSILON) * 100) / 100;
        proportions_text = "1:" + numratio + " " + this.translate("Ratio");
        if (this.proportions == "fibonacci")
          proportions_text += this.translate(
            "<br>The proportion is linked to the Fibonacci Sequence"
          );
        proportions_fibo_active = true;
      } else if (r >= 7.8 && r <= 8.2) {
        proportions_text = "1:8 " + this.translate("Ratio");
        if (this.proportions == "fibonacci")
          proportions_text += this.translate(
            "<br>The proportion is linked to the Fibonacci Sequence"
          );
        proportions_fibo_active = true;
      } else if (r >= 1.4 && r <= 1.58) {
        proportions_text = "2:3 " + this.translate("Ratio");
        if (this.proportions == "fibonacci")
          proportions_text += this.translate(
            "<br>The proportion is linked to the Fibonacci Sequence"
          );
        proportions_fibo_active = true;
      } else if (r >= 1.641 && r <= 1.7) {
        proportions_text = "3:5 " + this.translate("Ratio");
        if (this.proportions == "fibonacci")
          proportions_text += this.translate(
            "<br>The proportion is linked to the Fibonacci Sequence"
          );
        proportions_fibo_active = true;
      } else if (r >= 1.581 && r <= 1.64) {
        proportions_text = "1:1.681 " + this.translate("Golden Ratio");
        if (this.proportions == "fibonacci")
          proportions_text += this.translate(
            "<br>The proportion is the absolute Fibonacci Sequence"
          );
        proportions_fibo_active = true;
      }
      return proportions_fibo_active;
      return (
        _.indexOf(
          [3, 5, 8, 13, 21, 34, 55, 89, 144, 233, 377, 610],
          this.logo_shapes
        ) !== -1
      );
    },
