          break;
        case 2:
          title = this.translate("Do not add a drop shadow to the logo");
          const regex = /(<path)|(<rect)|(<circle)|(<polygon)/gm;
          const subst = `$& filter="url(#f3)"`;
          var svg = this.primary_logo().logo;

          svg = svg.replace(
            "</svg>",
            `<defs><filter id="f3" x="-20%" y="-20%" width="140%" height="140%" filterUnits="objectBoundingBox" primitiveUnits="userSpaceOnUse" color-interpolation-filters="sRGB">
							<feDropShadow stdDeviation="` +
              border_w +
              ` ` +
              border_w +
              `" in="SourceGraphic" dx="` +
              border_w +
              `" dy="` +
              border_w +
              `" flood-color="#1F3646" flood-opacity="1"	 x="0%" y="0%" width="100%" height="100%" result="dropShadow"/>
							</filter></defs></svg>`
          );
          svg = svg.replace(regex, subst);
          logo = svg;
          logo_b64 = "data:image/svg+xml;base64," + btoa(logo);
          break;
        case 3:
          title = this.translate("Do not make the logo bolder");
          var svg = this.primary_logo().logo;
          var svg_q = $(svg);
          var this_ = this;
          if ($(svg_q).find("defs").length == 0)
            $(svg_q).append("<defs></defs>");
          $(svg_q)
            .find("path, rect, circle, polygon")
            .each(function () {
              var attr_fill = $(this).attr("fill");
              var style_fill = $(this)[0].style.fill;
              var class_name = $(this)[0].className.baseVal;
              var color = attr_fill || style_fill;
              if (color.trim() == "" || typeof color === "undefined") {
                if (class_name != "" && typeof class_name !== "undefined") {
                  var style = $(svg_q).find("style").text();
                  const regex = new RegExp(
                    "." + class_name + "{.*fill:(.*);.*}",
                    "m"
                  );

                  let m;

                  if ((m = regex.exec(style)) !== null) {
                    // The result can be accessed through the `m`-variable.
                    color = m[1];
                  }
                } else {
                  color = this_
                    .rgbToHex($(this).attr("fill") || $(this).css("fill"))
                    .replace("#", "");
                }
              }

              $(this)
                .attr("stroke", "#" + color.replace("#", ""))
                .attr("stroke-width", border_w * 2 + "px")
                .css({
                  stroke: "#" + color.replace("#", ""),
                  strokeWidth: border_w * 2 + "px !important",
                });
            });
          var svg_index = 0;
          $(svg_q).each((index, item) => {
            if (item.tagName == "svg") svg_index = index;
