        case 5:
          title = this.translate("Do not rotate the logo to any angle");
          style = {
            transform: "rotate(45deg)",
            transformOrigin: "center",
          };
          break;
      }
      return {
        logo: logo,
        style: style,
        title: title,
        logo_b64: logo_b64,
        id: title,
      };
