    approve_variation_item() {
      const reg = /(#[0-9A-Fa-f]{3,6})/gm;
      const reg_rgb = /rgb\([0-9 ]{1,10},[0-9 ]{1,10},[0-9 ]{1,10}\)/gms;
      const reg_rgba =
        /rgba\([0-9 ]{1,10},[0-9 ]{1,10},[0-9 ]{1,10},([0-9. ]{1,10})\)/gms;
      const reg_named_color =
        /(amaranth|amber|amethyst|apricot|aquamarine|azure|baby|beige|brick|black|blue|blue|blue|blush|bronze|brown|burgundy|byzantium|carmine|cerise|cerulean|champagne|chartreuse|chocolate|cobalt|coffee|copper|coral|crimson|cyan|desert|electric|emerald|erin|gold|gray|green|harlequin|indigo|ivory|jade|jungle|lavender|lemon|lilac|lime|magenta|magenta|maroon|mauve|navy|ochre|olive|orange|orange|orchid|peach|pear|periwinkle|persian|pink|plum|prussian|puce|purple|raspberry|red|red|rose|ruby|salmon|sangria|sapphire|scarlet|silver|spring|spring|taupe|teal|turquoise|ultramarine|violet|viridian|white|yellow)/gm;
      const reg_fill_stroke =
        /(fill:)|(fill=")|(fill=\')|(fill :)|(stroke=")|(stroke:)|(stroke :)|(stroke=\')/m;
      const reg_some_path_no_style =
        /<path(?=\s|>)(?!(?:[^>=]|=(['"])(?:(?!\1).)*\1)*?\sclass=['"])(?!(?:[^>=]|=(['"])(?:(?!\1).)*\1)*?\sfill=['"])(?!(?:[^>=]|=(['"])(?:(?!\1).)*\1)*?\sstroke=['"])[^>]*\/>/gm;
      const reg_some_rect_no_style =
        /<rect(?=\s|>)(?!(?:[^>=]|=(['"])(?:(?!\1).)*\1)*?\sclass=['"])(?!(?:[^>=]|=(['"])(?:(?!\1).)*\1)*?\sfill=['"])(?!(?:[^>=]|=(['"])(?:(?!\1).)*\1)*?\sstroke=['"])[^>]*\/>/gm;
      const reg_some_circle_no_style =
        /<circle(?=\s|>)(?!(?:[^>=]|=(['"])(?:(?!\1).)*\1)*?\sclass=['"])(?!(?:[^>=]|=(['"])(?:(?!\1).)*\1)*?\sfill=['"])(?!(?:[^>=]|=(['"])(?:(?!\1).)*\1)*?\sstroke=['"])[^>]*\/>/gm;

      var title = "";
      var background = "transparent";

      //Math.floor(this.approve_variation_number / 4)
      var logo_id = this.approve_variation_number % this.logo_versions.length;
      var index_of_iteration = Math.floor(
        this.approve_variation_number / this.logo_versions.length
      ); //this.approve_variation_number % 4 - this.approve_variation_number % this.logo_versions.length
      var logo_tmp =
        this.logo_versions[
          this.approve_variation_number % this.logo_versions.length
        ] != undefined
          ? this.logo_versions[
              this.approve_variation_number % this.logo_versions.length
            ].logo
          : this.logo;

      if (!reg_fill_stroke.test(logo_tmp)) {
        var reg_svg = /<svg /m;
        logo_tmp = logo_tmp.replace(reg_svg, '<svg fill="#000000" ');
      }
      var paths_with_no_style = logo_tmp.match(reg_some_path_no_style);
      if (paths_with_no_style != null && paths_with_no_style.length > 0)
        paths_with_no_style.forEach((path) => {
          logo_tmp = logo_tmp.replace(
            path,
            path.replace("<path ", '<path fill="#000000" ')
          );
        });
      var rects_with_no_style = logo_tmp.match(reg_some_rect_no_style);
      if (rects_with_no_style != null && rects_with_no_style.length > 0)
        rects_with_no_style.forEach((path) => {
          logo_tmp = logo_tmp.replace(
            path,
            path.replace("<rect ", '<rect fill="#000000" ')
          );
        });
      var circle_with_no_style = logo_tmp.match(reg_some_circle_no_style);
      if (circle_with_no_style != null && circle_with_no_style.length > 0)
        circle_with_no_style.forEach((path) => {
          logo_tmp = logo_tmp.replace(
            path,
            path.replace("<circle ", '<circle fill="#000000" ')
          );
        });

      switch (index_of_iteration) {
        case 0:
          logo_tmp = logo_tmp
            .replace(reg, "#000")
            .replace(reg_rgb, "rgb(0,0,0)")
            .replace(reg_rgba, "rgba(0,0,0,$1)")
            .replace(reg_named_color, "#000");
          // console.log(logo_tmp)
          title = this.translate("Black & White Positive");
          break;
        case 1:
          logo_tmp = logo_tmp
            .replace(reg, this.main_color)
            .replace(reg_rgb, this.hexToRgb(this.main_color))
            .replace(reg_rgba, this.hexToRgba(this.main_color) + ",$1)")
            .replace(reg_named_color, this.main_color);
          // console.log(logo_tmp)
          title = this.translate("Primary Color Positive");
          break;
        case 2:
          logo_tmp = logo_tmp
            .replace(reg, this.secondary_color)
            .replace(reg_rgb, this.hexToRgb(this.secondary_color))
            .replace(reg_rgba, this.hexToRgba(this.secondary_color) + ",$1)")
            .replace(reg_named_color, this.secondary_color);
          title = this.translate("Secondary Color Positive");
          break;
        case 3:
          logo_tmp = logo_tmp
            .replace(reg, "#fff")
            .replace(reg_rgb, "rgb(255,255,255)")
            .replace(reg_rgba, "rgba(255,255,255,$1)")
            .replace(reg_named_color, "#fff");
          background = this.main_color;
          title = this.translate("White Negative");
          break;
      }
      return {
        logo: logo_tmp,
        logo_b64: "data:image/svg+xml;base64," + btoa(logo_tmp),
        title: title,
        background: background,
        id: logo_id + "-" + index_of_iteration,
      };
    },
