            .replace(reg, "#fff")
            .replace(reg_rgb, "rgb(255,255,255)")
            .replace(reg_rgba, "rgba(255,255,255,$1)")
            .replace(reg_named_color, "#fff");
          background = this.secondary_color;
          title = "Secondary Negative in Square";
          break;
        case 3:
          logo_tmp = logo_tmp
            .replace(reg, "#fff")
            .replace(reg_rgb, "rgb(255,255,255)")
            .replace(reg_rgba, "rgba(255,255,255,$1)")
            .replace(reg_named_color, "#fff");
          border_radius = "50%";
          background = this.secondary_color;
          title = "Secondary Negative in Circle";
          break;
      }
      return {
        icon: logo_tmp,
        icon_b64: "data:image/svg+xml;base64," + btoa(logo_tmp),
        title: title,
        border_radius: border_radius,
        background: background,
        id: title,
      };
    },
    color_selected() {
      this.show_color_picker = false;
      // this.colors_list.push(this.color_picker_colors)
    },
    can_color_scroll_right() {
      if (document.querySelector(".step") !== null)
        var max_steps = document.querySelector(".step").offsetWidth / 188;
      else max_steps = 0;
      if (this.colors_list < max_steps) return false;
      if (8 - this.colors_panel_scroll > max_steps) return true;
      return false;
    },
  },
  watch: {
    step(n, o) {
      var app = this;
      if (n == 24 && o != 24 && this.$route.params.pdf_links == undefined) {
        if (app.process_save_request == true) {
          var genInterval = setInterval(function () {
            if (app.process_save_request == false) {
              app.processGeneratePdf();
              clearInterval(genInterval);
            }
          }, 500);
          this.processing_inteval = setInterval(() => {
            if (this.processingWidth >= 10) {
              clearInterval(this.processing_inteval);
            }
            this.processingWidth++;
          }, 500);
        } else {
          app.processGeneratePdf();
        }
      }
      if (n == 24 && o != 24 && this.$route.params.pdf_links != undefined) {
        // this.pdf_links = this.$route.params.pdf_links
        this.forward();
      }
      // if(n==26 && o!=26){
      // this.$router.push({ name: 'Preview', params: {id: this.id}})
      // }
    },
    industry_level_1() {
      this.related_industries_1_loading = true;
      axios
        .get(
          this.endpoints.ajax.get.industries_related +
            "?industry=" +
            this.industry_level_1
        )
        .then((response) => {
          this.related_industries_1 = response.data;
          this.related_industries_1_loading = false;
        });
    },
    industry_level_2() {
      this.related_industries_2_loading = true;
      axios
        .get(
