            "?industry=" +
            this.industry_level_2
        )
        .then((response) => {
          this.related_industries_2 = response.data;
          this.related_industries_2_loading = false;
        });
    },
    colors_list() {
      if (typeof this.colors_list[0] !== "undefined") {
        this.main_color = this.colors_list[0].id;
        this.main_color_hist = this.main_color;
      }
      if (typeof this.colors_list[1] !== "undefined") {
        this.secondary_color = this.colors_list[1].id;
        this.secondary_color_hist = this.secondary_color;
      }
      if (typeof this.colors_list[2] !== "undefined") {
        this.third_color = this.colors_list[2].id;
        this.third_color_hist = this.third_color;
      }
    },
    color_scheme_mode() {
      this.generate_colors();
    },
    proportions() {
      this.proportion_logo_loaded();
    },
    space() {
      this.proportion_logo_loaded();
    },
    size() {
      this.proportion_logo_loaded();
    },
  },
};
